mapper.select:
select * from(select @finalnum:=@finalnum+1 as finalnum, sub.* from (select @rownum:=@rownum+1 as rownum, b.* from BOARD b join MEMBER m on b.MEMBER_SEQ = m.MEMBER_SEQ where (@rownum:=0)=0 and b.del = 0 and b.board_auth = 1 order by WRITE_DATE desc) sub, (select @finalnum:=0) ftmp order by sub.rownum desc) fsub
TITLE like CONCAT('%', #{searchWord}, '%')
order by fsub.finalnum desc limit #{start}, #{limit}

mapper.update:
UPDATE BOARD
SET READ_COUNT = READ_COUNT + 1
WHERE BOARD_SEQ = #{boardSeq}

mapper.select:
select *
from BOARD
where BOARD_AUTH = 1 and CATEGORY =0 and BOARD_SEQ = #{boardSeq}

mapper.delete:
update BOARD
set DEL = 1
where BOARD_SEQ = #{boardSeq}

mapper.update:
update FINAL.BOARD_COMMENT
SET CONTENT = #{content}
WHERE BOARD_COMMENT_SEQ = #{boardCommentSeq}

update FINAL.BOARD_COMMENT
SET DEL = 1
WHERE BOARD_COMMENT_SEQ = #{boardCommentSeq}

mapper.select:
select REF, STEP, DEPTH
from FINAL.BOARD_COMMENT
where BOARD_COMMENT_SEQ = #{boardCommentSeq}
